properties.query:
let LearningPeriod = 5d;
let RunTime = 1h;
let StartLearningPeriod = LearningPeriod + RunTime;
let EndRunTime = RunTime - 1d;
let BinTime = 1h;
let NumOfStdsThreshold = 3;
let MinThreshold = 5.0;
let MinLearningBuckets = 5;
let TrafficLogs = (AzureDiagnostics | where TimeGenerated  between (ago(StartLearningPeriod) .. ago(EndRunTime)) | where OperationName == "AzureFirewallApplicationRuleLog" or OperationName == "AzureFirewallNetworkRuleLog" | parse msg_s with * "from " srcip ":" srcport " to " dsturl ":" dstport ". Action: " action "." * | where action == "Deny" | where isnotempty(dsturl) and isnotempty(srcip));
let LearningSrcIpDenyRate = (TrafficLogs | where TimeGenerated between (ago(StartLearningPeriod) .. ago(RunTime)) | summarize count() by srcip, bin(TimeGenerated, BinTime), dsturl | summarize LearningTimeSrcIpDenyRateAvg = avg(count_), LearningTimeSrcIpDenyRateStd = stdev(count_), LearningTimeBuckets = count() by srcip, dsturl | where LearningTimeBuckets > MinLearningBuckets); 
let AlertTimeSrcIpDenyRate = (TrafficLogs | where TimeGenerated between (ago(RunTime) .. ago(EndRunTime)) | summarize AlertTimeSrcIpDenyRateCount = count() by srcip);
AlertTimeSrcIpDenyRate | join kind=leftouter (LearningSrcIpDenyRate) on srcip | extend LreaningThreshold = max_of(LearningTimeSrcIpDenyRateAvg + NumOfStdsThreshold * LearningTimeSrcIpDenyRateStd, MinThreshold) | where AlertTimeSrcIpDenyRateCount > LreaningThreshold | project-away srcip1, LearningTimeSrcIpDenyRateAvg, LearningTimeSrcIpDenyRateStd | extend IPCustomEntity = srcip, URLCustomEntity = dsturl
