properties.query:
let RunTime = 1h; 
let StartRunTime = 1d; 
let EndRunTime = StartRunTime - RunTime; 
let MinAffectedThershold = 5; 
AzureDiagnostics | where TimeGenerated  between (ago(StartRunTime) .. ago(EndRunTime))  | parse msg_s with * "from " srcip ":" srcport " to " dsturl ":" dstport  "." * "ThreatIntel: " ThreatIntel  | where OperationName == "AzureFirewallThreatIntelLog" | summarize TiTrafficCount = count(), dCountSourceIps = dcount(srcip), AffectedIps = make_set(srcip) by dsturl, ThreatIntel, srcip | where dCountSourceIps > MinAffectedThershold | order by TiTrafficCount desc | extend IPCustomEntity = srcip, URLCustomEntity = dsturl
