properties.query:
let RunTime = 1h;
let StartRunTime = 1d;
let EndRunTime = StartRunTime - RunTime;
let MinimumDifferentPortsThreshold = 100;
let BinTime = 30s;
AzureDiagnostics | where TimeGenerated  between (ago(StartRunTime) .. ago(EndRunTime)) | where OperationName == "AzureFirewallApplicationRuleLog" or OperationName == "AzureFirewallNetworkRuleLog" | parse msg_s with * "from " srcip ":" srcport " to " dsturl ":" dstport | where isnotempty(dsturl) and isnotempty(srcip) | summarize AlertTimedCountPortsInBinTime = dcount(dstport) by srcip, bin(TimeGenerated, BinTime), dsturl | where AlertTimedCountPortsInBinTime > MinimumDifferentPortsThreshold | extend IPCustomEntity = srcip, URLCustomEntity = dsturl
