properties.query:
let RunTime = 1h;
let StartRunTime = 1d;
let EndRunTime = StartRunTime - RunTime;
let MinimumDifferentHostsThreashold = 200;
let ExcludedPorts = dynamic([80, 443]);
let BinTime = 30s;
AzureDiagnostics | where TimeGenerated  between (ago(StartRunTime) .. ago(EndRunTime)) | where OperationName == "AzureFirewallApplicationRuleLog" or OperationName == "AzureFirewallNetworkRuleLog" | parse msg_s with * "from " srcip ":" srcport " to " dsturl ":" dstport | where dstport !in (ExcludedPorts) | where isnotempty(dsturl) and isnotempty(srcip) and isnotempty(dstport) | summarize AlertTimedCountHostsInBinTime = dcount(dsturl) by srcip, bin(TimeGenerated, BinTime), dstport, dsturl | where AlertTimedCountHostsInBinTime > MinimumDifferentHostsThreashold | extend IPCustomEntity = srcip, URLCustomEntity = dsturl
